tinymce.PluginManager.add('anchor',function(a){function s(){var b=a.selection.getNode(),n='';if(b.tagName=='A'){n=b.name||b.id||'';}a.windowManager.open({title:'Anchor',body:{type:'textbox',name:'name',size:40,label:'Name',value:n},onsubmit:function(e){a.execCommand('mceInsertContent',false,a.dom.createHTML('a',{id:e.data.name}));}});}a.addCommand('mceAnchor',s);a.addButton('anchor',{icon:'anchor',tooltip:'Anchor',onclick:s,stateSelector:'a:not([href])'});a.addMenuItem('anchor',{icon:'anchor',text:'Anchor',context:'insert',onclick:s});});
