			if (this._syncing) {
				return false;
			}

			this._syncing = true;
			var vs = this._viewStateManager;
			var vsm = this._vsmSelected;
			var sel = this._selected;

			for (var i in sel) {
				if (sel.hasOwnProperty(i)) {
					var id = this._indexToNodeId(i);
					if (id == null || id == "") {
						continue;
					}

					var issel = sel[i];// tree.isIndexSelected(i);

					if (this._toggled[i]) {
						issel = !issel;
					}

					vs.setSelectionState(id, issel);
					sel[i] = issel;
					vsm[id] = issel;
				}
			}
			
			this._toggled = {};

			this._syncing = false;
