			var tree = this._tree;
			var selCount = 0;
			var lastSel = -1;
			
			this._selected = {};

			for (var i = 0; ; i++) {
				var id = this._indexToNodeId(i);
				if (id == null || id == "") {
					break;
				}

				var sel = vs.getSelectionState(id);

				if (sel) {
					this._selected[i] = true;
					selCount++;
					lastSel = i;
				}

				if (sel != tree.isIndexSelected(i)) {
					if (sel) {
						tree.addSelectionInterval(i, i);
					} else {
						tree.removeSelectionInterval(i, i);
					}
				}
			}
			
